import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, LineElement, CategoryScale, LinearScale, Title, Tooltip, Legend } from "chart.js";
import './LiveCurveCard.css'; // Import the CSS file

// Register chart.js components
ChartJS.register(LineElement, CategoryScale, LinearScale, Title, Tooltip, Legend);

const LiveCurveCard = () => {
  const [data, setData] = useState([]);
  const [labels, setLabels] = useState([]);

  // Function to simulate live data fetching (replace with real API or socket data)
  const fetchLiveData = () => {
    const newData = Math.random() * 100; // Random data to simulate live data
    const currentTime = new Date().toLocaleTimeString();

    // Add new data and label to the state
    setData((prevData) => [...prevData, newData].slice(-10)); // Keep last 10 data points
    setLabels((prevLabels) => [...prevLabels, currentTime].slice(-10)); // Keep last 10 labels
  };

  // Set an interval to fetch new data every 2 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchLiveData();
    }, 2000); // 2 seconds interval

    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  // Chart.js data
  const chartData = {
    labels: labels,
    datasets: [
      {
        label: "Live Data",
        data: data,
        fill: false,
        borderColor: "rgba(75,192,192,1)",
        tension: 0.1,
      },
    ],
  };

  return (
    <div className="live-curve-card">
      <h3 className="card-title">Live Data Curve</h3>
      <div className="chart-container">
        <Line data={chartData} options={{ responsive: true }} />
      </div>
    </div>
  );
};

export default LiveCurveCard;
