import React, { useState } from 'react';
import './UserTable.css'; // Import the CSS file

const UserTable = ({ users }) => {
  // Handle checkbox change for status (for all users)
  const handleStatusChange = (userId) => {
    const updatedUsers = users.map((user) => {
      if (user.id === userId) {
        return {
          ...user,
          status: user.status === "active" ? "inactive" : "active",
        };
      }
      return user;
    });
    // setUsers(updatedUsers);  // Update the state with the modified users
  };

  return (
    <div className="card">
      <h3>User Information</h3>
      <table className="table">
        <thead>
          <tr>
            <th className="cell">Name</th>
            <th className="cell">Email</th>
            <th className="cell">Department</th>
            <th className="cell">Organization ID</th>
            <th className="cell">Secret</th>
            <th className="cell">Status</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user.id}>
              <td className="cell">{user.name}</td>
              <td className="cell">{user.email}</td>
              <td className="cell">{user.department}</td>
              <td className="cell">{user.orgId}</td>
              <td className="cell">{user.secret}</td>
              <td className="cell">
                <input
                  type="checkbox"
                  checked={user.status === "active"}
                  onChange={() => handleStatusChange(user.id)}
                />
                <label style={{ marginLeft: "8px" }}>
                  {user.status === "active" ? "Active" : "Inactive"}
                </label>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserTable;
